mapper.select:
select

from HT
where 1=1

  and  HTFL = #{htfl,jdbcType=VARCHAR}


  and  QSRQ >= to_date(#{startTime,jdbcType=VARCHAR}, 'yyyy-mm-dd')


  and  QSRQ <= to_date(#{endTime,jdbcType=VARCHAR}, 'yyyy-mm-dd ')


  and  FZR = #{fzr,jdbcType=VARCHAR}


  and  SSFZR = #{ssfzr,jdbcType=VARCHAR}


  and  HTZT = #{htzt,jdbcType=VARCHAR}


  and  DQSHENG = #{dqsheng,jdbcType=VARCHAR}


  and  DQSHI = #{dqshi,jdbcType=VARCHAR}


  and  HTNRHTNR <= #{htjemax,jdbcType=DECIMAL}


  and  HTNRHTNR > #{htjemin,jdbcType=DECIMAL}

select

from HT
where
FZRID = #{fzrid,jdbcType=DECIMAL}

  and  HTFL = #{htfl,jdbcType=VARCHAR}


  and  QSRQ >= to_date(#{startTime,jdbcType=VARCHAR}, 'yyyy-mm-dd')


  and  QSRQ <= to_date(#{endTime,jdbcType=VARCHAR}, 'yyyy-mm-dd ')


  and  SSFZR = #{ssfzr,jdbcType=VARCHAR}


  and  HTZT = #{htzt,jdbcType=VARCHAR}


  and  DQSHENG = #{dqsheng,jdbcType=VARCHAR}


  and  DQSHI = #{dqshi,jdbcType=VARCHAR}


  and  HTNRHTNR <= #{htjemax,jdbcType=DECIMAL}


  and  HTNRHTNR > #{htjemin,jdbcType=DECIMAL}

select

from HT
where
SSFZRID = #{ssfzrid,jdbcType=DECIMAL}

  and  HTFL = #{htfl,jdbcType=VARCHAR}


  and  QSRQ >= to_date(#{startTime,jdbcType=VARCHAR}, 'yyyy-mm-dd')


  and  QSRQ <= to_date(#{endTime,jdbcType=VARCHAR}, 'yyyy-mm-dd ')


  and  FZR = #{fzr,jdbcType=VARCHAR}


  and  HTZT = #{htzt,jdbcType=VARCHAR}


  and  DQSHENG = #{dqsheng,jdbcType=VARCHAR}


  and  DQSHI = #{dqshi,jdbcType=VARCHAR}


  and  HTNRHTNR <= #{htjemax,jdbcType=DECIMAL}


  and  HTNRHTNR > #{htjemin,jdbcType=DECIMAL}

mapper.update:
update HT
set HTZT = #{htzt,jdbcType=VARCHAR}
where ID = #{id,jdbcType=DECIMAL}
