mapper.select:
select httime,count(*) as count,count(dqshi) as dqcount,sum(htje) as sum,round(avg(htje),2) as avg from ( select to_char(qsrq, 'YYYY-MM') as httime,htnrhtnr as htje,dqshi from ht where 1=1 and TO_CHAR(qsrq,'YYYY-MM')=TO_CHAR(SYSDATE,'YYYY-MM')
and FZR = #{fzr,jdbcType=VARCHAR}
) group by httime order by httime

select httime,count(*) as count,count(dqshi) as dqcount,sum(htje) as sum,round(avg(htje),2) as avg from ( select to_char(qsrq, 'YYYY-MM') as httime,htnrhtnr as htje,dqshi from ht where 1=1 and TO_CHAR(qsrq,'YYYY-MM')=TO_CHAR(SYSDATE,'YYYY-MM')
and SSFZR = #{ssfzr,jdbcType=VARCHAR}
) group by httime order by httime

select httime,count(*) as count,count(dqshi) as dqcount,sum(htje) as sum,round(avg(htje),2) as avg from ( select to_char(qsrq, 'YYYY-MM') as httime,htnrhtnr as htje,dqshi from ht where 1=1 and TO_CHAR(qsrq,'YYYY-MM')=TO_CHAR(SYSDATE,'YYYY-MM')
and FZR in (select xm from users where ssbm =(select ssbm from users where xm=#{fzr,jdbcType=VARCHAR}) )
) group by httime order by httime

select to_char(qsrq, 'YYYY-MM') as httime,htnrhtnr as htje,dqshi,dqsheng from ht
where 1=1

    and TO_CHAR(qsrq,'YYYY-MM') =  #{month,jdbcType=VARCHAR}


    and FZR = #{fzr,jdbcType=VARCHAR}


    and SSFZR = #{ssfzr,jdbcType=VARCHAR}


    and FZR in (select xm from users where ssbm =(select ssbm from users where xm=#{bmjl,jdbcType=VARCHAR}) )

select to_char(htqs.sj, 'YYYY-MM') as httime,htqs.SFSKWC as ifsk,htqs.JE as fkje,htqs.HTBH as htbh,ht.fzr as fzr,htqs.ssfzr
from htqs left join ht on ht.htbh=htqs.htbh
where 1=1

    and TO_CHAR(htqs.sj,'YYYY-MM') =  #{month,jdbcType=VARCHAR}


    and ht.FZR = #{fzr,jdbcType=VARCHAR}


    and htqs.SSFZR = #{ssfzr,jdbcType=VARCHAR}


    and ht.FZR in (select xm from users where ssbm =(select ssbm from users where xm=#{bmjl,jdbcType=VARCHAR}) )

select to_char(htqs.sj, 'YYYY-MM') as httime,htqs.SFSKWC as ifsk,htqs.JE as fkje,ht.yymc,
    htqs.HTBH as htbh,ht.fzr as fzr,ht.dqsheng as dqsheng,ht.dqshi as dqshi,
    htqs.ssfzr as ssfzr,htfl.flmc as flmc from htqs
left join ht on ht.htbh=htqs.htbh
left join htfl on htfl.id=ht.htfl
where 1=1
--负责人部门名称

    and ht.fzr in (select xm from users where ssbm=#{fzrbm,jdbcType=VARCHAR})

--负责人名称

    and ht.FZR = #{fzr,jdbcType=VARCHAR}

--实施负责人部门名称

    and htqs.ssfzr in (select xm from users where ssbm=#{ssfzrbm,jdbcType=VARCHAR})

--实施负责人名称

    and htqs.SSFZR = #{ssfzr,jdbcType=VARCHAR}

--合同分类

    and htfl.flmc = #{htfl,jdbcType=VARCHAR}


    and ht.DQSHENG = #{dqsheng,jdbcType=VARCHAR}


    and ht.DQSHI = #{dqshi,jdbcType=VARCHAR}


    and to_char(htqs.sj, 'yyyy-mm-dd') >= #{startTime,jdbcType=VARCHAR}


    and to_char(htqs.sj, 'yyyy-mm-dd') <= #{endTime,jdbcType=VARCHAR}

order by htqs.sj

select HTQS.ID as ID, HTQS.HTBH AS HTBH, HTQS.QS AS QS, nvl(ht.htnrhtnr,0) AS JE, HTQS.YJSJ AS YJSJ, HTQS.SGYQ AS SGYQ, HTQS.SFSKWC AS SFSKWC, nvl(HTQS.JE,0) AS SKJE, HTQS.CREATE_BY AS CREATE_BY, HTQS.UPDATE_BY AS UPDATE_BY, HTQS.CREATE_TIME AS CREATE_TIME, HTQS.UPDATE_TIME AS UPDATE_TIME, HTQS.SJ AS SJ, HTQS.SSFZR AS SSFZR, HTQS.SSFZRID AS SSFZRID,HTQS.BZ AS BZ, htfl.flmc as flmc,HT.DQSHENG AS SHENG,HT.DQSHI AS SHI,HT.FZR AS FZR,ht.yymc as yymc from HTQS left join ht on ht.htbh=htqs.htbh left join htfl on htfl.id=ht.htfl where 1=1 --负责人部门名称
and HT.fzr in (select xm from users where ssbm=#{fzrbm,jdbcType=VARCHAR})
--负责人名称
and HT.FZR = #{fzr,jdbcType=VARCHAR}
--实施负责人部门名称
and HTQS.ssfzr in (select xm from users where ssbm=#{ssfzrbm,jdbcType=VARCHAR})
--实施负责人名称
and HTQS.SSFZR = #{ssfzr,jdbcType=VARCHAR}
--合同分类
and htfl.flmc = #{htfl,jdbcType=VARCHAR}
--合同编号
and htqs.htbh like concat(concat('%',#{htbh,jdbcType=VARCHAR}),'%')
and HT.DQSHENG = #{dqsheng,jdbcType=VARCHAR}
and HT.DQSHI = #{dqshi,jdbcType=VARCHAR}
and to_char(HTQS.SJ, 'yyyy-mm-dd') >= #{startTime,jdbcType=VARCHAR}
and to_char(HTQS.SJ, 'yyyy-mm-dd') <= #{endTime,jdbcType=VARCHAR}
order by HTQS.SJ

select HT.ID AS ID,HTBH, QSRQ, FZR, YYMC, HTNR, HTNRHTNR, FKZQ, NYWFJE, NYWFSJ, XXKXM, XXKLXFS, CWKXM, CWKLXFS, YWDJR, YWDJRLXFS, SFSKWC, SKJE, HTSFDQ, HTZT, BZ, YYJB, DQSHENG,DQSHI, HTFL.FLMC AS HTFL, HT.CREATE_BY AS CREATE_BY, HT.UPDATE_BY AS UPDATE_BY, HT.CREATE_TIME AS CREATE_TIME, HT.UPDATE_TIME AS UPDATE_TIME, FZRID,SSFZR,SSFZRID,HTQSRQ,HTZZRQ,YQTS,HTSYTS,HTMC,SFGB,SFJXFW,YJQYSJ,XMQKSM from HT LEFT JOIN HTFL ON HTFL.ID=HT.HTFL where 1=1 --负责人部门名称
and fzr in (select xm from users where ssbm=#{fzrbm,jdbcType=VARCHAR})
--负责人名称
and FZR = #{fzr,jdbcType=VARCHAR}
--实施负责人部门名称
and ssfzr in (select xm from users where ssbm=#{ssfzrbm,jdbcType=VARCHAR})
--实施负责人名称
and SSFZR = #{ssfzr,jdbcType=VARCHAR}
--合同分类
and htfl.flmc = #{htfl,jdbcType=VARCHAR}
and DQSHENG = #{dqsheng,jdbcType=VARCHAR}
and DQSHI = #{dqshi,jdbcType=VARCHAR}
and to_char(qsrq, 'yyyy-mm-dd') >= #{startTime,jdbcType=VARCHAR}
and to_char(qsrq, 'yyyy-mm-dd') <= #{endTime,jdbcType=VARCHAR}
order by QSRQ

select httime,count(*) as count,sum(htje) as sum,round(avg(htje),3) as avg from
(select to_char(qsrq, 'YYYY-MM') as httime,htnrhtnr as htje from ht left join htfl on ht.htfl=htfl.id where 1=1

    and htfl.flmc = #{htfl,jdbcType=VARCHAR}


    and QSRQ >= to_date(#{startTime,jdbcType=VARCHAR}, 'yyyy-mm-dd')


    and QSRQ <= to_date(#{endTime,jdbcType=VARCHAR}, 'yyyy-mm-dd ')


    and FZR = #{fzr,jdbcType=VARCHAR}

--负责人部门名称

    and fzr in (select xm from users where ssbm=#{fzrbm,jdbcType=VARCHAR})


    and SSFZR = #{ssfzr,jdbcType=VARCHAR}

--实施负责人部门名称

    and ssfzr in (select xm from users where ssbm=#{ssfzrbm,jdbcType=VARCHAR})


    and DQSHENG = #{dqsheng,jdbcType=VARCHAR}


    and DQSHI = #{dqshi,jdbcType=VARCHAR}


    and to_char(qsrq, 'yyyy-mm-dd') >= #{startTime,jdbcType=VARCHAR}


    and to_char(qsrq, 'yyyy-mm-dd') <= #{endTime,jdbcType=VARCHAR}

)
group by httime order by httime

select htfl,count(htfl) as count,to_char(count(htfl)*100/sum(htfl),'fm990.00')||'%' as percent
   from (select htfl,to_char(qsrq, 'YYYY-MM') as httime from ht)
group by htfl order by htfl

select count(*) as count,sum(htje) as sum,round(avg(htje),2) as avg,htfl as htfl from
(select to_char(ht.qsrq, 'YYYY-MM') as httime,htfl.flmc as htfl,ht.htnrhtnr as htje from ht,htfl where
ht.htfl=htfl.id

    and htfl.flmc = #{htfl,jdbcType=VARCHAR}


    and ht.QSRQ >= to_date(#{startTime,jdbcType=VARCHAR}, 'yyyy-mm-dd')


    and ht.QSRQ <= to_date(#{endTime,jdbcType=VARCHAR}, 'yyyy-mm-dd ')


    and ht.FZR = #{fzr,jdbcType=VARCHAR}

--负责人部门名称

    and ht.fzr in (select xm from users where ssbm=#{fzrbm,jdbcType=VARCHAR})


    and ht.SSFZR = #{ssfzr,jdbcType=VARCHAR}

--实施负责人部门名称

    and ht.ssfzr in (select xm from users where ssbm=#{ssfzrbm,jdbcType=VARCHAR})


    and ht.DQSHENG = #{dqsheng,jdbcType=VARCHAR}


    and ht.DQSHI = #{dqshi,jdbcType=VARCHAR}


    and to_char(ht.qsrq, 'yyyy-mm-dd') >= #{startTime,jdbcType=VARCHAR}


    and to_char(ht.qsrq, 'yyyy-mm-dd') <= #{endTime,jdbcType=VARCHAR}

)
group by htfl order by htfl
